import React, { useState, useEffect } from 'react';
import Layout from './Layout';
import axios from 'axios';

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [recentTasks, setRecentTasks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const [statsResponse, tasksResponse] = await Promise.all([
        axios.get('/api/stats'),
        axios.get('/api/tasks?limit=5')
      ]);

      if (statsResponse.data.success) {
        setStats(statsResponse.data.stats);
      }

      if (tasksResponse.data.success) {
        setRecentTasks(tasksResponse.data.tasks.slice(0, 5));
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Layout>
        <div className="loading">
          <div className="spinner"></div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="page-header">
        <h1 className="page-title">Dashboard</h1>
        <p className="page-subtitle">Welcome back! Here's an overview of your tasks.</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon" style={{color: 'var(--primary)'}}>
            <i className="fas fa-tasks"></i>
          </div>
          <div className="stat-number" style={{color: 'var(--primary)'}}>
            {stats?.total || 0}
          </div>
          <div className="stat-label">Total Tasks</div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{color: 'var(--success)'}}>
            <i className="fas fa-check-circle"></i>
          </div>
          <div className="stat-number" style={{color: 'var(--success)'}}>
            {stats?.completed || 0}
          </div>
          <div className="stat-label">Completed</div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{color: 'var(--warning)'}}>
            <i className="fas fa-clock"></i>
          </div>
          <div className="stat-number" style={{color: 'var(--warning)'}}>
            {stats?.pending || 0}
          </div>
          <div className="stat-label">Pending</div>
        </div>

        <div className="stat-card">
          <div className="stat-icon" style={{color: 'var(--primary)'}}>
            <i className="fas fa-spinner"></i>
          </div>
          <div className="stat-number" style={{color: 'var(--primary)'}}>
            {stats?.inProgress || 0}
          </div>
          <div className="stat-label">In Progress</div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">Recent Tasks</h2>
          <a href="/tasks" className="btn btn-outline">
            <i className="fas fa-eye"></i>
            View All
          </a>
        </div>
        <div className="card-body">
          {recentTasks.length === 0 ? (
            <div className="text-center" style={{padding: '2rem', color: 'var(--text-light)'}}>
              <i className="fas fa-inbox" style={{fontSize: '3rem', marginBottom: '1rem'}}></i>
              <p>No tasks yet. Create your first task to get started!</p>
              <a href="/create-task" className="btn btn-primary" style={{marginTop: '1rem'}}>
                <i className="fas fa-plus"></i>
                Create Task
              </a>
            </div>
          ) : (
            <div className="task-list">
              {recentTasks.map((task) => (
                <div key={task.id} className={`task-item priority-${task.priority}`}>
                  <div className="task-header">
                    <div>
                      <h3 className="task-title">{task.title}</h3>
                      <div className="task-meta">
                        <span className={`status-badge status-${task.status.replace('-', '-')}`}>
                          {task.status.replace('-', ' ')}
                        </span>
                        <span className={`priority-badge priority-${task.priority}`}>
                          {task.priority}
                        </span>
                        <span>
                          <i className="fas fa-calendar"></i>
                          {new Date(task.dueDate).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  </div>
                  <p className="task-description">{task.description}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      <div style={{display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1.5rem', marginTop: '2rem'}}>
        <div className="card">
          <div className="card-header">
